WebLink.url:
{!REQUIRESCRIPT("/soap/ajax/42.0/connection.js")}

var selectedRecords = {!GETRECORDIDS($ObjectType.Task)};
if (selectedRecords.length == 0) {
    alert("Please select at least one task to delete.");
} else {
    if (confirm("Are you sure you want to delete the selected tasks?")) {
        var result = sforce.connection.deleteIds(selectedRecords);
        if (result[0].getBoolean("success")) {
            alert("The selected tasks have been deleted.");
        } else {
            alert("Error deleting tasks: " + result[0].errors.message);
        }
        location.reload();
    }
}
